SELECT custom_code,device,lot_id,cp_step,wafer_no,site_id,soft_bin_no,bin_count,pass_fail FROM
vtdatester.bin_info_summary
WHERE custom_code=#{customerCode} AND device=#{device} AND lot_id=#{lot} AND cp_step=#{cp} AND test_type='F'

    AND wafer_no=#{waferId}

;

SELECT custom_code,device,lot_id,cp_step,wafer_no,site_id,soft_bin_no,bin_count,pass_fail FROM
vtdatester.bin_info_summary
WHERE custom_code=#{customerCode} AND device=#{device} AND lot_id=#{lot} AND cp_step=#{cp} AND test_type=#{type} AND wafer_no=#{waferId}
;

SELECT wafer_no,others_params FROM vtdatester.bin_wafer_summary WHERE custom_code=#{customerCode}

    AND device=#{device}


    AND lot_id=#{lot}


    AND cp_step=#{cp}


    AND wafer_no=#{waferId}

;

SELECT
custom_code,device,lot_id,cp_step,wafer_no,oprator,tester_program,tester_id,prober_id,Prober_card_id,pid_name,pid_version,start_time,gross_die,pass_die,fail_die,yield,end_time,map_cols,map_rows,min_x,min_y
FROM vtdatester.bin_wafer_summary WHERE custom_code=#{custom}

    AND device=#{device}


    AND lot_id=#{lot}


    AND cp_step=#{cp}


    AND wafer_no=#{waferId}

;
